# ISBNコードから書籍情報を取得する関数
def get_book_info_by_isbn(isbn):
    url = f'https://www.googleapis.com/books/v1/volumes?q=isbn:{isbn}'
    response = requests.get(url).json()

    if response['totalItems'] == 0:
        return 'No book found for this ISBN'

    book_info = response['items'][0]['volumeInfo']

    #getメソッドで取得できない場合は、第二引数の値を返す
    #取得するデータによってはJSONの構造が異なるため、getメソッドを使って安全にデータを取得する
    #   ->取得出来なかった場合は第二引数の値のエラーメッセージになってしまう
    title = book_info.get('title', 'No title available')#タイトル
    authors = book_info.get('authors', 'No authors available')#著者
    published_date = book_info.get('publishedDate', 'No published date available')#出版日

    page_count = book_info.get('pageCount', 'No page count available')#ページ数
    #volume_info = book_info.get('volumeInfo', {})
    #page_count = volume_info.get('pageCount', 'No page count available')

    description = book_info.get('description', 'No description available')#要約
    publisher = book_info.get('publisher', 'No publisher information available')#出版社
    image_links = book_info.get('imageLinks', {})#画像リンク
    cover_image_url = image_links.get('thumbnail', 'No cover image available')#画像

    items_list = response['items'] #items リストデータ
    items = items_list[0] #items
    selfLink = items['selfLink']#リンク...取得データすべてを閲覧できるリンク
    print(title + 'selfLink:', selfLink)
    print('------------------------')

    return {
        'title': title,#タイトル
        'isbn_13': isbn,#ISBN-13コード
        'authors': authors,#著者
        'published_date': published_date,#出版日
        'page_count': page_count,#ページ数
        'description': description,#要約
        'publisher': publisher,#出版社
        'cover_image_url': cover_image_url#画像URL
    }

# 書籍情報をJSON形式でファイルに書き込む関数
def write_to_json(data, filename):
    with open(filename, 'w', encoding='utf-8') as f:
        json.dump(data, f, ensure_ascii=False, indent=4)

